mapper.select:
select * from tb_link
where id = #{id}
order by no asc

mapper.insert:
insert into tb_link (
	id, title, url, category, memo	
) values (
	#{id}, #{title}, #{url}, #{category}, #{memo}
)

mapper.update:
update tb_link
	set title = #{title}
		, url = #{url}
		, category = #{category}
		, memo = #{memo}
	where id = #{id}
	and no = #{no}

mapper.select:
select * from tb_link
	where id = #{id}
	and no = #{no}

mapper.delete:
delete from tb_link
	where id = #{id}
	and no = #{no}
