mapper.select:
select * from tb_video
where id = #{id}
order by no asc

mapper.insert:
insert into tb_video (
	id, title, url
) values (
	#{id}, #{title}, #{url} 
)

mapper.update:
update tb_video
	set title = #{title}
		content = #{content}
	where id = #{id}
	and no = #{no}

mapper.delete:
delete from tb_video
	where id = #{id}
	and no = #{no}
